 CREATE PROCEDURE [dbo].[P_AlterarContasReceberDI]

	@ObsTit varchar(500),
	@Situacao varchar(5),
	@codclie varchar(16),
	@Numtit varchar(15),  
	@Seqtit  varchar(20),
	@dtEmissao DateTime,
	@IdFilial varchar(3)
AS
	UPDATE Contas_a_Receber SET ObsTit = @ObsTit, Situacao = @Situacao
	WHERE codclie = @codclie AND 
		  Numtit = @Numtit AND 
		  Seqtit = @Seqtit AND 
		  dtEmissao = @dtEmissao AND 
		  IdFilial = @IdFilial

